import React from 'react'
import Helmet from 'react-helmet'
import { useSelector } from 'redux/hook'
//import { isMobileAction } from 'redux/app/actions'
import styled from 'styled-components'
import { Row, Col, Icon, Pagination } from 'antd'
import { Left } from 'components'
import Link from 'next/link'
import { LazyLoadImage } from 'react-lazy-load-image-component'

const EventRow = styled.div`
  line-height: 1.75;
  margin-bottom: 16px;
  border-radius: 2px;
  background: #fff;
  border: 1px solid #eaedf3;
  a {
    display: block;
    color: #283941;
  }
  @media only screen and (max-width: 992px) {
    margin-bottom: 8px;
  }
`
const EventPicture = styled.div`
  width: 100%;
  padding-top: 70%;
  position: relative;
  @media only screen and (max-width: 576px) {
    padding-top: 75%;
  }
  img {
    position: absolute;
    bottom: 0px;
    width: 100%;
    height: 100%;
    left: 0px;
    object-fit: cover;
    object-position: center center;
    right: 0px;
    top: 0px;
  }
`
const EventPictureLoading = styled.div`
  position: absolute;
  bottom: 0px;
  width: 100%;
  height: 100%;
  left: 0px;
  right: 0px;
  top: 0px;
  background: #fafafa;
  i.anticon-loading {
    color: #0046bb;
    float: right;
    margin: 15px;
    font-size: 1em;
  }
`
const EventTitle = styled.div`
  overflow: hidden;
  text-overflow: ellipsis;
  display: -webkit-box;
  -webkit-line-clamp: 1;
  -webkit-box-orient: vertical;
  h2 {
    color: #3e3f42;
    font-size: 1.1em;
    margin: 0;
  }
`
const EventDetail = styled.div`
  padding: 6px;
  i {
    color: #0046bb;
  }
`
const EventProvince = styled.div`
  span {
    font-size: 14px;
    color: #0046bb;
  }
`
const EventTime = styled.div`
  color: #3a8bbb;
  margin-top: 2px;
  font-size: 0.85em;
`
const EventRowKM = styled.div`
  position: absolute;
  z-index: 1;
  right: 3px;
  bottom: 6px;
  display: flex;
`
const EventRowCol = styled.div`
  position: relative;
  float: left;
  width: 30px;
  margin-right: 5px;
  border-bottom-left-radius: 2px;
  border-bottom-right-radius: 2px;
  border: 1px solid #0046bb;
  div {
    &:first-child {
      background: #4b8eff;
      color: #fff;
      font-size: 8px;
      text-align: center;
    }
    &:last-child {
      font-size: 12px;
      font-weight: bold;
      background: #fff;
      color: #333;
      text-align: center;
      line-height: 1.4;
      box-shadow: 0 8px 24px 0 rgba(0, 0, 0, 0.11);
    }
  }
`
const EventTabs = styled.ul`
  margin: 0;
  padding: 0;
  margin-bottom: 1em;
  background-color: #fff;
  border: 1px solid #eaedf3;
  display: flex;
  -webkit-overflow-scrolling: touch;
  -ms-overflow-style: -ms-autohiding-scrollbar;
  white-space: nowrap;
  overflow-x: auto;
  overflow-y: hidden;
  &::-webkit-scrollbar {
    display: none;
  }
  &::-moz-scrollbar {
    display: none;
  }
  @-moz-document url-prefix() {
    overflow-x: scroll;
    scrollbar-width: none;
  }
  li {
    float: left;
    display: inline-flex;
    a {
      color: #283941;
      padding: 10px 16px;
      &:hover {
        color: #2063c7;
      }
      &.active {
        color: #2063c7;
        padding: 9px 16px;
        border-bottom: 2px solid #0046bb;
      }
    }
    i {
      margin-right: 8px;
    }
  }
`
const EventPagination = styled(Pagination)`
  padding: 10px 0;
  @media only screen and (max-width: 992px) {
    text-align: center;
  }
  .ant-pagination-prev:not(.ant-pagination-disabled),
  .ant-pagination-next:not(.ant-pagination-disabled),
  .ant-pagination-jump-prev,
  .ant-pagination-jump-next {
    a {
      background-color: #ffffff;
    }
  }
  .ant-pagination-item-link {
    border-radius: 2px;
  }
  .ant-pagination-item {
    border-radius: 2px;
    background-color: #ffffff;
    &.ant-pagination-item-active {
      background: #4b8cfc;
      border-color: #4b8cfc;
      & a {
        color: #fff;
      }
    }
  }
`

